export interface SmartReporterOptions {
    outputFile?: string;
    historyFile?: string;
    maxHistoryRuns?: number;
    performanceThreshold?: number;
    slackWebhook?: string;
    teamsWebhook?: string;
    enableRetryAnalysis?: boolean;
    enableFailureClustering?: boolean;
    enableStabilityScore?: boolean;
    enableGalleryView?: boolean;
    enableComparison?: boolean;
    enableAIRecommendations?: boolean;
    stabilityThreshold?: number;
    retryFailureThreshold?: number;
    baselineRunId?: string;
}
export interface TestHistoryEntry {
    passed: boolean;
    duration: number;
    timestamp: string;
    skipped?: boolean;
    retry?: number;
}
export interface RunSummary {
    runId: string;
    timestamp: string;
    total: number;
    passed: number;
    failed: number;
    skipped: number;
    flaky: number;
    slow: number;
    duration: number;
    passRate: number;
    ciInfo?: CIInfo;
}
export interface RunMetadata {
    runId: string;
    timestamp: string;
}
export interface TestHistory {
    runs: RunMetadata[];
    tests: {
        [testId: string]: TestHistoryEntry[];
    };
    summaries?: RunSummary[];
}
export interface CIInfo {
    provider: string;
    branch?: string;
    commit?: string;
    buildId?: string;
}
export interface StepData {
    title: string;
    duration: number;
    category: string;
    isSlowest?: boolean;
}
export interface TestResultData {
    testId: string;
    title: string;
    file: string;
    status: 'passed' | 'failed' | 'skipped' | 'timedOut' | 'interrupted';
    duration: number;
    error?: string;
    retry: number;
    flakinessScore?: number;
    flakinessIndicator?: string;
    performanceTrend?: string;
    averageDuration?: number;
    aiSuggestion?: string;
    steps: StepData[];
    screenshot?: string;
    videoPath?: string;
    tracePath?: string;
    traceData?: string;
    history: TestHistoryEntry[];
    retryInfo?: RetryInfo;
    failureCluster?: FailureCluster;
    stabilityScore?: StabilityScore;
    attachments?: AttachmentData;
    performanceMetrics?: PerformanceMetrics;
}
export interface RetryInfo {
    totalRetries: number;
    passedOnRetry: number;
    failedRetries: number;
    retryPattern: boolean[];
    needsAttention: boolean;
}
export interface FailureCluster {
    id: string;
    errorType: string;
    count: number;
    tests: TestResultData[];
    aiSuggestion?: string;
}
export interface StabilityScore {
    overall: number;
    flakiness: number;
    performance: number;
    reliability: number;
    grade: 'A' | 'B' | 'C' | 'D' | 'F';
    needsAttention: boolean;
}
export interface AttachmentData {
    screenshots: string[];
    videos: string[];
    traces: string[];
}
export interface PerformanceMetrics {
    averageDuration: number;
    currentDuration: number;
    percentChange: number;
    absoluteChange: number;
    threshold: number;
    isRegression: boolean;
    isImprovement: boolean;
    severity: 'low' | 'medium' | 'high';
}
export interface RunComparison {
    baselineRun: RunSummary;
    currentRun: RunSummary;
    changes: ComparisonChanges;
}
export interface ComparisonChanges {
    newFailures: TestResultData[];
    fixedTests: TestResultData[];
    newTests: TestResultData[];
    regressions: TestResultData[];
    improvements: TestResultData[];
}
export interface TestRecommendation {
    type: 'flakiness' | 'retry' | 'performance' | 'cluster' | 'suite';
    priority: number;
    title: string;
    description: string;
    action: string;
    affectedTests: string[];
    icon: string;
}
export interface GalleryItem {
    id: string;
    testTitle: string;
    testId: string;
    status: string;
    dataUri?: string;
    videoPath?: string;
    tracePath?: string;
}
export interface SuiteStats {
    total: number;
    passed: number;
    failed: number;
    skipped: number;
    flaky: number;
    slow: number;
    needsRetry: number;
    passRate: number;
    averageStability: number;
}
